INSERT INTO `services` (`id`, `name`, `description`, `slug`, `deleted`) VALUES
(1, 'Ágyéki gerinc MR vizsgálat', 'Az ágyéki vagy lumbális (lumbosacralis) gerincszakasz MR vizsgálatával meghatározhatóak a gerincoszlop elváltozásai, a porckorong betegségei, a csontvelő és a csigolya környéki eltérések. Segítségével ellenőrizhetők a műtétek utáni hegek. MR vizsgálatot a lumbális szakaszon leggyakrabban porckorongsérv (discus hernia) és fejlődési rendellenességek gyanúja esetén végezzük.', 'Agyeki-gerinc-mr-vizsgalat', 0),
(2, 'Belső fül CT vizsgálat', 'Ezzel a CT vizsgálattal a belső fül finom csontszerkezetét és a hallójáratokat látja a szakember. Krónikus gyulladások esetén, a hallócsontokat is ellenőrizhetjük vele, valamint a légtartóssági viszonyok vizsgálatára is alkalmas. A CT, azaz komputertomográfia egy speciális röntgen vizsgálat, mely a röntgensugarakat felhasználva készít anatómiailag pontos felvételeket az emberi testről és a belső szervekről.', 'belso-ful-ct-vizsgalat', 0),
(3, 'Azonnali sürgős ellátás', 'Akár már enyhe tünetek is komoly betegséget jelezhetnek. Ha időben orvoshoz fordulunk, a szakszerű segítség és a korai diagnosztika súlyos következményektől menthet meg.  Enyhe mellkasi vagy hasi fájdalom, felső légúti tünetek, láz, felfázás, deréktáji panaszok, vagy akár háztartási balesetek esetén is forduljon hozzánk! Orvosaink, szakképzett asszisztencia, sürgős labor-, röntgen- és CT-vizsgálatok várakozás nélkül rendelkezésre állnak. Gyógyszertárunk is elérhető a rendelési idő alatt.', 'azonnali-surgos-ellatas', 0),
(4, 'Az első szülész-nőgyógyász szakorvosi vizsgálat', 'Lehetőség szerint a 12. várandóssági hétig javasolt rá sort keríteni. Ez a vizsgálat szolgál a várandósgondozásba vétel alapjául, és ekkor történik a szülés várható időpontjának az elsődleges meghatározása is, melynek része a méhen belüli várandósság igazolása bimanuális vizsgálattal, hüvelyi ultrahangos vizsgálattal, esetleg laboratóriumi várandóssági próbával, valamint kolposzkópia, amennyiben esedékes cytologiai mintavétel, emlők vizsgálata, testsúly, testmagasságmérés, tanácsadás.', 'az-elso-szulesz-nogyogyasz-szakorvosi-vizsgalat', 0),
(5, 'Nyugalmi EKG', ' A nyugalmi EKG vizsgálat semmiféle előkészületet nem igényel a páciens részéről, vizsgálat közben a páciens hanyatt fekszik, a törzsére és a végtagjaira elektródákat helyeznek. A vizsgálat a szívműködést kísérő elektromos tevékenységről, annak változásairól ad képet. Az EKG-görbét egy monitoron jeleníti meg a készülék, melyet papírszalagra rajzolt görbék formájában is kinyomtat. A vizsgálat közben nem lehet sem mozogni, sem beszélni, végig nyugodt pozícióban kell maradni.', 'nyugalmi-ekg', 0),
(6, 'Urológiai szakorvosi vizsgálat', 'Az urológiai szakorvosi vizsgálat során lehetőség van a vese, húgyhólyag, húgycső és húgyvezeték fejlődési rendellenességeinek, daganatainak, köveinek, gyulladásos megbetegedéseinek kivizsgálására, valamint a férfi nemi szervek, prosztata, ondóhólyag, hímvessző, here daganatainak, a prosztata jóindulatú megnagyobbodásának, a férfi nemi szervek működésének, gyulladásos megbetegedéseinek kivizsgálására és kezelésére. Urológusaink rendelkezésre állunk vizelettartási, ürítési zavarok, férfi klimax, merevedési zavar, korai magömlés kivizsgálására és kezelésére is.', 'urologiai-szakorvosi-vizsgalat', 0),
(7, 'Gasztroenterológiai szakorvosi vizsgálat', 'Az emésztőszervi problémák súlyossága sokszor nem áll arányban a panaszok súlyosságával: enyhe panasz vagy tünet hátterében állhat akár súlyos betegség, illetve fordítva, sokszor komoly vagy riadalmat keltő panaszokat enyhe elváltozások okoznak. Ezért fontos, hogy ismételt panaszok esetén szakorvoshoz forduljunk, aki megfelelően tudja értékelni a panaszokat és tüneteket, és elvégzi a szükséges kivizsgálást.', 'gasztroenterologiai-szakorvosi-vizsgalat', 0),
(8, 'Krónikus migrén kezelése', 'A krónikus migrén, a szem körüli vagy a nyakizmok akaratlan és ismétlődő izomösszehúzódása botulinum toxin injekcióval eredményesen kezelhető. Bár a botox-kezelésről sokaknak a ránctalanság jut eszébe elsőre, korántsen csupán sztár-hóbort vagy szépészeti eljárás. A botox-kezelés csökkentheti a fejfájások, tikkelések, izomrángások mértékét és gyakoriságát, sokszor teljesen megszüntetve a problémát.', 'kronikus-migren-kezelese', 0),
(9, 'Kardiológiai szakorvosi vizsgálat + szív ultrahang', 'Szívünk mesterien kivitelezett pumpa, melyet apró elektromos automatika tart működésben. Az impulzusokat a pitvar falában található szinusz csomó hozza létre. A komplex kardiológiai kivizsgálás azt hivatott feltérképezni, hogy van-e bármilyen eltérés vagy meghibásodás a szív működésében, a pontos diagnózist így célzott kezelés követheti.', 'kardiologiai-szakorvosi-vizsgalat-+-sziv-ultrahang', 0),
(10, 'Gerincgyógyászat', 'Az egyik leggyakoribb mozgásszervi panasz, amellyel reumatológushoz, ortopéd szakorvoshoz, vagy gerincgyógyászhoz (újabban alternatív medicinákhoz) fordulnak hazánkban a derékfájdalom, illetve a nyaki vagy háti panaszok, időnként végtagfájdalommal párosulva. A magyar lakosság majdnem 80 százaléka szenved életében legalább egyszer valamilyen gerinctáji fájdalomtól. Ezeken a tüneteken túl a gerincoszlop egyéb kóros elváltozásai, komplex mozgásszervi eltérések, deformitások, korrekciók és a végtagi fájdalomszindrómák is a gerincgyógyász ellátási köréhez tartozhatnak.', 'gerincgyogyaszat', 0),
(11, 'Proktológia', 'A proktológia az általános sebészet speciális szakterülete, mely a végbél betegségeinek vizsgálatával és kezelésével foglalkozik. A proktológus vizsgálja az aranyérbetegséget (külső, belső, illetve trombotizált aranyeres csomókat), a végbélnyílás környékének gyulladásos folyamatait (tályogok, sipolyok), jóindulatú daganatait (polipok), a végbél alsó szakaszának és az ánusznyílásnak a különböző egyéb elváltozásait (végbélrepedés, krónikus fájdalom, viszketés).', 'proktologia', 0),
(12, 'Testplasztika', 'Akár a kor, a várandósság vagy a testsúlyingadozás hagy nyomot a testen, az esztétikus látvány testplasztika segítségével újra megteremthető. Plasztikai sebészeink a legkorszerűbb eljárásokkal és végleges alakformáló technikákkal gondoskodnak az ideális testkép eléréséről. Hasplasztikai, combplasztikai és vádliplasztikai, illetve további területeket érintő beavatkozásokat is végzünk. Természetesen, ha kifejezetten zsírleszívásra van szüksége, abban is készséggel állunk a rendelkezésére.', 'testplasztika', 0),
(13, 'Arcplasztika', 'Az arcplasztikai beavatkozások azok számára lehet ideális választás, akik fiatalabb megjelenésre és feszesebb arcvonásokra vágynak. Sajnos az öregedés jelei egy idő után már semmilyen arcápoló készítménnyel vagy esztétikai beavatkozással nem javíthatók jelentősen, ezért ilyenkor a plasztikai sebészet nyújthat hosszú távon is valóban látványos eredményt. Szakorvosainkkal kizárólag a legmodernebb sebészeti eljárásokkal dolgozunk. Magánkórházunkban az arcfelvarráson kívül szemhéj- és orrplasztikai, továbbá fül- és nyakplasztikai beavatkozások is elérhetők. Érdemes kiemelni a saját zsírral való feltöltést is, amely természetes módon, mesterséges hatóanyagok nélkül segít elérni a fiatalos arcvonásokat. Jelentkezzen konzultációra, és tudjon meg többet a beavatkozásokról!', 'arcplasztika', 0);
